import React from 'react'
import { Icon } from "antd";
import Header from '../Header/index';
import Footer from '../Footer/index';

import { connect } from 'react-redux';
import { fetchTaskRequest } from '../../../actions/index';
import callApi from '../../../Services/callAPI';

class DashBoard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tasks: []
        }
    }

    componentDidMount() {
        callApi('todos', 'GET', null, null).then(res => {
            console.log('fetchTasks',res.data)
            this.props.fetchTasks(res.data)
        })
    }

    render() {
        const { tasks } = this.state;
        console.log('tasks', tasks)
        return (
            <div>
                <Header />
                <div>
                    <p>Đây là dashboard</p>
                    <Icon type="smile" />
                </div>
                <Footer />
            </div>
        )
    }
}

//map state cua store thanh du lieu cua props
const mapStateToProps = (state) => {
    return {
        tasks: state.vidu1Reducer
    }
}

//map ham post task vao props
const mapDispatchToProps = (dispatch) => {
    return {
        fetchTask: (tasks) => {
            dispatch(fetchTask(tasks));
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DashBoard);
