import React from 'react'
import { Icon } from "antd";
import Header from '../Header/index';
import Footer from '../Footer/index';

import { connect } from 'react-redux';
import {fetchTasks} from '../../../actions/index';

class DashBoard extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            tasks: []
        }
    }
    render() {
        const {DanhSachSanPham} = this.props;
        console.log('DanhSachSanPham',DanhSachSanPham)
        return (
            <div>
                <Header />
                <div>
                    <p>Đây là dashboard</p>
                    <Icon type="smile" />
                </div>
                <Footer />
            </div>
        )
    }
}

//map state cua store thanh du lieu cua props
const mapStateToProps = (state) => {
    return {
        DanhSachSanPham: state.vidu1Reducer
    }
}

//map ham post task vao props
const mapDispatchToProps = (dispatch) => {
    return {
        fetchTasks: (tasks) => {
            dispatch(fetchTasks(tasks));
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DashBoard);
