import React from 'react'
import { Row, Col, Button } from "antd";
import Header from '../Header/index';
import Footer from '../Footer/index';
import "./styles.css";

import { connect } from 'react-redux';  //connect toi store
import { fetchTaskRequest } from '../../../actions/index';

import {
    BrowserRouter as Router,
    Switch,
    Route,
} from "react-router-dom";
// import route client
import Intro from './../Intro/index';

class DashBoard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tasks: []
        }
    }

    componentDidMount() {
        this.props.fetchTaskRequest();
    }

    render() {
        const { tasks } = this.props
        console.log('tasks', tasks)
        return (
            <div>
                {/* header giữ nguyên */}
                <Header />
                <div className="dashboard">
                    <div className="main-dashboard">
                        {/* màn hình sẽ thay đổi ở đây */}
                        <Row gutter={16}>
                            <Col span={16}>
                                <div className="dashboard-left">
                                    <Router>
                                        <div>
                                            <Switch>
                                                {/* route */}
                                                <Route path="/intro" component={Intro} />
                                            </Switch>
                                        </div>
                                    </Router>
                                </div>
                            </Col>
                            <Col span={8}>
                                <div className="dashboard-right">
                                    col-8
                                </div>
                            </Col>
                        </Row>
                    </div>
                </div>
                {/* footer giữ nguyên */}
                <Footer />
            </div>
        )
    }
}

// lay tat ca du lieu task tu store
const mapStateToProps = (state) => {
    return {
        tasks: state.vidu1Reducer
    }
}

// luu du lieu len store
const mapDispatchToProps = (dispatch) => {
    return {
        fetchTaskRequest: (tasks) => {
            dispatch(fetchTaskRequest(tasks));
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DashBoard);
