// khoi tao co tham so
let initialState = {
    id: '1',
    value: 'A'
}

//truyen co tham so
let myReducer = (state = initialState, action) => {
    if (action.type === 'COTHAMSO') {
        let { id, value } = action.data1 //get data1 from action -- id = action.data1.id
        let { status } = state // status = state.status
        return {id,value}
    }
    return state;
}

export default myReducer;
